mapper.select:
select * from oa_market_act where cont_state=1 AND terminal=2 AND now()>eff_date and exp_date>now() and cont_type IN
#{list}
ORDER BY create_time DESC

select DISTINCT FROM oa_market_act ma inner join oa_market_agent mm on ma.act_id = mm.act_id WHERE mm.agent_id = #{agentId} and ma.cont_state = 2 and ma.terminal in ('2','3') AND ma.shop_id IN (SELECT shop_id FROM oa_factory_info fac WHERE fac.fact_id IN
#{list}
) and ma.shop_id IN (SELECT ofi.shop_id FROM t_agent_fac taf inner JOIN oa_factory_info ofi on taf.factory_id = ofi.fact_id WHERE agent_id = #{agentId} and taf.status = 1) and ma.create_time <= NOW() order by ma.create_time desc

select DISTINCT FROM oa_market_act ma inner join oa_market_agent mm on ma.act_id = mm.act_id INNER JOIN oa_market_goods omg ON omg.act_id = ma.act_id INNER JOIN t_goods go ON omg.goods_id = go.id AND go.prod_type = 2 AND go.is_market = 1 AND go.terminal IN (2, 3)
WHERE mm.agent_id = #{agentId} and cont_state = 2 AND ma.shop_id IN (SELECT shop_id FROM oa_factory_info fac WHERE fac.fact_id IN
#{list}
) and ma.shop_id IN (SELECT ofi.shop_id FROM t_agent_fac taf inner JOIN oa_factory_info ofi on taf.factory_id = ofi.fact_id WHERE agent_id = #{agentId} and taf.status = 1) and ma.eff_date <= NOW() order by ma.create_time desc

select

FROM
  oa_market_act ma
WHERE
    ma.shop_id = #{ownShopid}

        and ma.cont_type = #{contType}


        and ma.cont_state = #{contState}


     and ma.push_time >= #{effDate,jdbcType=DATE}


     and ma.push_time <= #{expDate,jdbcType=DATE}

    order by ma.push_time desc
