mapper.select:
SELECT oam.`agent_id` AS agentId, CASE WHEN cl.level_name IS NULL THEN '' ELSE cl.level_name END AS agentLevel, oam.agent_name AS agentName, oam.link_name AS linkName, oam.link_tel AS linkTel, oam.agent_addr AS agentAddr, af.sys_id AS shopId, af.factory_id AS factoryId FROM oa_agent_mas oam INNER JOIN t_agent_fac af ON af.`agent_id` = oam.`agent_id` AND af.`status` = 1 INNER JOIN t_member m ON m.`own_shopid` = af.`sys_id` AND m.`id` = #{memberId} LEFT JOIN crm_cust_level cl ON cl.`level_id` = af.`level_id` WHERE 1 = 1
AND cl.level_id = #{levelId}
AND oam.agent_name like #{namePattern}
AND oam.link_tel like #{telPattern}
ORDER BY af.agent_no DESC

select f.factory_id,c.band_id,c.band_name,c.pic_path band_pic_path,d.list_id,d.list_name,d.pic_path list_pic_path,m.model_id,m.model_name,e.cat_id,e.cat_name,IFNULL(e.cat_desc,e.cat_name) cat_desc ,e.pic_path cat_pic_path,e.pcat_id,e.is_fac from oa_agent_band_lists a
left join crm_prd_list_cats b on a.list_id=b.list_id
join crm_cust_band c on a.band_id=c.band_id
join crm_prd_list d on a.band_id=d.band_id and a.list_id=d.list_id
join crm_prd_model m on d.list_id=m.list_id
left join crm_prd_cat e on b.cat_id=e.cat_id and e.cat_state=1
join t_agent_fac f on a.agent_id=f.agent_id and a.fact_id=f.factory_id
where c.band_state=1 and d.list_state=1 and m.model_state=1
and a.agent_id=#{agentId}

    and f.factory_id=#{factoryId}

and f.`status`=1


    and c.band_name like #{pbKey} and c.band_id in (select  brand_id from t_goods where is_market='1' and terminal in(2,3) )



    and d.list_name like #{plKey} and d.list_id in ( select  list_id from t_goods where is_market='1' and terminal in(2,3) )



    and m.model_name like #{pmKey}



    and e.cat_name like #{pcKey} and e.cat_id in ( select DISTINCT cat_id from t_goods_cat where goods_id
    in(select id from t_goods where is_market='1' and terminal in('2','3')
    /*and stock>0  当库存为0的时候可以存在，只有当下架状态为0的时候才是真正的查询不到*/
    and shop_id in(select
    shop_id from oa_factory_info
    where fact_id=#{factoryId}

    )))

and a.band_id in (
select ds.brand_id from t_goods ds LEFT JOIN t_goods_level le
on ds.id = le.goods_id LEFT JOIN t_agent_fac fac
on fac.level_id = le.level_id
where le.status = 1 and fac.status = 1 and ds.is_market = 1 and ds.terminal in (2,3) AND fac.agent_id = #{agentId}

    and fac.factory_id=#{factoryId}

)
AND d.list_id in (
select ds.list_id from t_goods ds LEFT JOIN t_goods_level le
on ds.id = le.goods_id LEFT JOIN t_agent_fac fac
on fac.level_id = le.level_id
where le.status = 1 and fac.status = 1 and ds.is_market = 1 and ds.terminal in (2,3)
AND fac.agent_id = #{agentId}

    and fac.factory_id=#{factoryId}

)
group by a.band_id,a.list_id,m.model_id,e.cat_id;

SELECT * FROM (SELECT a.`agent_id`, af.`factory_id`, af.`level_id`, b.`band_id`, b.`band_name`, l.`list_id`, l.`list_name`, m.`model_id`, m.`model_name`, c.`cat_id`, c.`cat_name`, c.`cat_desc` FROM oa_agent_band_lists a JOIN crm_cust_band b ON b.`band_id` = a.`band_id` JOIN crm_prd_list l ON l.`band_id` = b.`band_id` JOIN crm_prd_model m ON m.`band_id` = l.`band_id` AND m.`list_id` = l.`list_id` JOIN crm_prd_list_cats lc ON lc.`list_id` = l.`list_id` JOIN crm_prd_cat c ON c.`cat_id` = lc.`cat_id` LEFT JOIN t_agent_fac af ON af.agent_id = a.`agent_id` and af.status = 1 and af.coop_state = 1 WHERE a.agent_id=#{agentId}
and a.fact_id=#{factoryId}
and b.band_name like #{pbKey}
and l.list_name like #{plKey}
and m.model_name like #{pmKey}
and ((c.cat_desc like #{pcKey} and c.is_fac = 1 ) or (c.cat_name like #{pcKey} and c.is_fac = 0))
and c.cat_name like #{pcKey}
GROUP BY af.`factory_id`, af.`level_id`, b.`band_id`, l.`list_id`, m.`model_id`, c.`cat_id`) v WHERE EXISTS (SELECT 1 FROM t_goods g
JOIN t_goods_cat gc ON gc.goods_id = g.id
JOIN t_goods_level gl ON gl.goods_id = g.id WHERE g.is_market = 1 and g.terminal in (2,3) and g.`brand_id` = v.band_id AND g.`list_id` = v.list_id AND g.`model_id` = v.model_id
AND gc.`cat_id` = v.cat_id
AND gl.`level_id` = v.level_id)

SELECT b.band_id, b.band_name, b.pic_path band_pic_path, c.list_id, c.list_name, c.pic_path list_pic_path, d.model_id, d.model_name, f.cat_id, f.cat_name, f.pic_path cat_pic_path, f.pcat_id, f.is_fac FROM t_goods g LEFT JOIN crm_cust_band b ON b.band_id=g.brand_id LEFT JOIN crm_prd_list c ON c.band_id=g.brand_id AND c.list_id=g.list_id LEFT JOIN crm_prd_model d ON d.band_id=c.band_id AND d.list_id = c.list_id LEFT JOIN t_goods_cat e ON e.goods_id=g.id LEFT JOIN crm_prd_cat f ON f.cat_id=e.cat_id WHERE g.is_market='1' and g.terminal in('2','3') AND g.id in (SELECT mg.goods_id FROM t_agent_fac af INNER JOIN oa_factory_info fi ON af.factory_id = fi.fact_id AND fi.fact_state = 1 INNER JOIN oa_market_act ma ON ma.shop_id = fi.shop_id AND ma.cont_state = 2 AND ma.terminal IN (2, 3) INNER JOIN oa_market_agent ag ON ag.agent_id = af.agent_id AND ag.act_id = ma.act_id INNER JOIN oa_market_goods mg ON mg.act_id = ma.act_id WHERE af.status = 1 AND af.agent_id = #{agentId}
and af.factory_id in
#{lid}
)
and b.band_name like #{pbKey}
and c.list_name like #{plKey}
and d.model_name like #{pmKey}
and ((f.cat_desc like #{pcKey} and f.is_fac = 1 ) or (f.cat_name like #{pcKey} and f.is_fac = 0))
and f.cat_name like #{pcKey}
group by g.brand_id,g.list_id,g.model_id,g.catagory_id;
