mapper.update:
UPDATE t_ads AS T SET T.id = #{id} ,
T.adstype = #{adstype} ,
T.catagory = #{catagory} ,
T.channel = #{channel} , T.sort = #{sort} , T.title = #{title} , T.content_type = #{contentType} , T.thumbnail = #{thumbnail} , T.content_path = #{contentPath} , T.ads_img = #{adsImg} , T.introduce = #{introduce} , T.ads_content = #{adsContent} , T.hits = #{hits} , T.is_top = #{isTop} ,
T.status = #{status} ,
T.remark = #{remark} , T.create_date = #{createDate} , T.modify_date = #{modifyDate}
, T.shop_id = #{shopId}
, T.adduser_id = #{addUserId}
WHERE T.ID = #{id}

mapper.select:
SELECT *  FROM t_ads AS T

     AND T.id = #{id} 
     AND T.adstype  = #{adstype} 
     AND T.catagory = #{catagory} 
     AND T.`status` = #{status} 
     AND T.adduser_id = #{addUserId} 
     AND T.shop_id = #{shopId} 
     AND T.`status` in(0,1)

SELECT *  FROM t_ads AS T

     AND T.id = #{id} 
     AND T.adstype  = #{adstype} 
     AND T.catagory = #{catagory} 
     AND T.`status` = #{status} 
     AND T.`shop_id` = #{shopId}

select a.* from t_ads AS a where a.shop_id in

  #{shopId}

  and a.adstype =0 and a.catagory =1
  LIMIT 0,${ids}
